// 为节点添加属性
function setAttribute(dom, name, value) {
  // 添加样式
  if (name === 'style') {
    return Object.entries(value).forEach(([key, val]) => dom .style[key] = typeof val === 'number' ? `${val}px` : val);
  }

  // 添加事件
  if (/^on\w+/.test(name)) {
    name = name.toLowerCase();
    return dom[name] = value || '';
  }

  // 添加 class
  if (name === 'className') {
    name = 'class';
  }

  dom.setAttribute(name, value)
}

// 创建组件
function createComponent(component, props) {
  let inst;
  // console.log(component, props)
  if (component.prototype && component.prototype.defaultProps) {
    Object.assign(props, component.prototype.defaultProps);
  }

  if (component.prototype && component.prototype.render) {
    inst = new component(props);
  } else {
    inst = new component(props);
    inst.constructor = component;
    inst.render = (params) => {
      return inst.constructor(params || props);
    }
  }

  return inst;
}

function setComponentProps(component, props) {
  // console.log(component, props)
  if (!component.base) {
    if (component.componentWillMount) {
      component.componentWillMount()
    }
  } else if (component.componentWillReceiveProps) {
    component.componentWillReceiveProps(props);
  }
  
  // console.log('renderC', component, component.render())
  component.props = props;

  renderComponent(component);
}

function renderComponent(component) {
  // console.log(component)
  const render = component.render(component.props);

  if (component.base && component.componentWillUpdate) {
    component.componentWillUpdate();
  }

  // const base = _render(render);
  // console.log(render)
  const base = diffNode(component.base, render);

  if (!component.base) {
    if (component.componentDidMount) {
      component.componentDidMount();
    }
  } else if (component.componentDidUpdate) {
    component.componentDidUpdate()
  }

  // if (component.base && component.base.parentNode) {
  //   component.base.parentNode.replaceChild(base, component.base);
  // }

  component.base = base;
  base._component = component;
}

export default {
  render: (vnode, container) => {
    container.innerHTML = '';
    return render(vnode, container);
  },
  renderComponent,
  setAttribute,
  setComponentProps,
  createComponent
}
